mapper.update:
update f_fund_info
set update_time = now()
,fund_name = #{fundName}
,fund_code = #{fundCode}
,max_net_worth_date = #{maxNetWorthDate}
,max_net_worth = #{maxNetWorth}
,fund_type_id = #{fundTypeId}
,sorting = #{sorting}
,volatility_value = #{volatilityValue}
where id = #{id}

update f_fund_info
set update_time = now()
  ,max_net_worth_date = #{maxNetWorthDate}
  ,max_net_worth = #{maxNetWorth}
  ,fund_name = #{fundName}
where fund_code = #{fundCode}

update f_fund_info
set bonus_net_worth = #{bonusNetWorth}
where fund_code = #{fundCode}

mapper.select:
select ffi.id, ffi.fund_name AS fundName, ffi.fund_code AS fundCode, ffi.max_net_worth_date AS maxNetWorthDate, ffi.max_net_worth AS maxNetWorth, ffi.fund_type_id AS fundTypeId, fft.fund_type_name AS fundTypeName, fft.fund_type_number AS fundTypeNumber, ffi.sorting, ffi.volatility_value AS volatilityValue from f_fund_info ffi left join f_fund_type fft on fft.id=ffi.fund_type_id
where ffi.fund_code=#{fundCode} and ffi.delete_status=0;
where ffi.id!=#{id} and ffi.fund_code=#{fundCode} and ffi.delete_status=0;
