mapper.select:
select count(*)
from f_fund_net_worth
where fund_info_code = #{fundCode}

mapper.update:
update f_fund_net_worth
set update_time      = now()
  , fund_type_name   = #{fundTypeName}
  , fund_type_number = #{fundTypeNumber}
  , sorting          = #{sorting}
where id = #{id}

update f_fund_net_worth_temp
set create_time      = now()
  , fund_net_worth = #{fundNetWorth}
  , fund_day = #{fundDay}
  , level_number          = #{levelNumber}
  , level_front          = #{levelFront}
  , level_behind          = #{levelBehind}
where fund_info_code = #{fundInfoCode}

mapper.select:
select id, fund_type_number AS fundTypeNumber, fund_type_name AS fundTypeName, sorting, create_time AS createTime, update_time AS updateTime from f_fund_net_worth
where (fund_type_number=#{fundTypeNumber} or fund_type_name=#{fundTypeName}) and delete_status=0;
where id!=#{id} and (fund_type_number=#{fundTypeNumber} or fund_type_name=#{fundTypeName}) and delete_status=0;
