mapper.select:
select uftp.id, uftp.fund_info_code as fundInfoCode, uuf.fund_info_name_number as fundInfoNameNumber, uuf.purchase_rate as purchaseRate, uuf.sell_rate as sellRate, ffi.fund_name as fundName, uftp.purchase_time as purchaseTime, uftp.purchase_rate_amount as purchaseRateAmount, uftp.purchase_share as purchaseShare, uftp.purchase_net_worth as purchaseNetWorth, uftp.purchase_amount as purchaseAmount, uftp.purchase_amount_all as purchaseAmountAll, uftp.yield as yield, uftp.income_amount as incomeAmount, uftp.today_time as todayTime, uftp.today_net_worth as todayNetWorth, ffnwt.level_number as levelNumber from u_fund_transaction_purchase uftp left join f_fund_info ffi on ffi.fund_code = uftp.fund_info_code left join u_user_fund uuf on uuf.fund_info_code = uftp.fund_info_code and uftp.user_id = uuf.user_id left join f_fund_net_worth_temp ffnwt on uftp.fund_info_code = ffnwt.fund_info_code where uftp.user_id = #{userId} and uftp.state = #{state}
and uftp.fund_info_code = #{fundCode}
and ffi.fund_name LIKE concat(concat("%",#{fundName}),"%"))
order by uftp.purchase_time desc,uftp.yield desc, uftp.fund_info_code limit #{page}, #{limit};
order by uftp.yield desc, uftp.purchase_time desc, uftp.fund_info_code limit #{page}, #{limit};

select count(*)
from u_fund_transaction_purchase uftp
left join f_fund_info ffi on ffi.fund_code = uftp.fund_info_code
where uftp.user_id = #{userId}
and uftp.state = #{state}

    and uftp.fund_info_code = #{fundCode}


    and ffi.fund_name LIKE concat(concat("%",#{fundName}),"%"))

select uftp.id, uftp.fund_info_code as fundInfoCode, uuf.fund_info_name_number as fundInfoNameNumber, uuf.purchase_rate as purchaseRate, uuf.sell_rate as sellRate, ffi.fund_name as fundName, uftp.purchase_time as purchaseTime, uftp.purchase_rate_amount as purchaseRateAmount, uftp.purchase_share as purchaseShare, uftp.purchase_net_worth as purchaseNetWorth, uftp.purchase_amount as purchaseAmount, uftp.yield as yield, uftp.income_amount as incomeAmount, uftp.today_time as todayTime, uftp.today_net_worth as todayNetWorth, ffnwt.level_number as levelNumber from u_fund_transaction_purchase uftp left join f_fund_info ffi on ffi.fund_code = uftp.fund_info_code left join u_user_fund uuf on uuf.fund_info_code = uftp.fund_info_code and uftp.user_id = uuf.user_id left join f_fund_net_worth_temp ffnwt on uftp.fund_info_code = ffnwt.fund_info_code
where uftp.user_id = #{userId} and uftp.state = #{state} and uftp.minimum_new_worth =1
and uftp.fund_info_code = #{fundCode}
and ffi.fund_name LIKE concat(concat("%",#{fundName}),"%"))
order by uftp.purchase_time desc, ffi.sorting , uftp.yield desc limit #{page}, #{limit};

select count(*)
from u_fund_transaction_purchase uftp
left join f_fund_info ffi on ffi.fund_code = uftp.fund_info_code
where uftp.user_id = #{userId}
and uftp.state = #{state}
and uftp.minimum_new_worth =1

    and uftp.fund_info_code = #{fundCode}


    and ffi.fund_name LIKE concat(concat("%",#{fundName}),"%"))

select ufts.id, ufts.fund_info_code as fundInfoCode, uuf.fund_info_name_number as fundInfoNameNumber, uuf.purchase_rate as purchaseRate, uuf.sell_rate as sellRate, ffi.fund_name as fundName, ufts.sell_time as sellTime, ufts.sell_amount as sellAmount, ufts.sell_rate_amount as sellRateAmount, ufts.sell_rate as sellRate, ufts.sell_amount_all as sellAmountAll, ufts.sell_share as sellShare, ufts.sell_net_worth as sellNetWorth, ufts.income_amount as incomeAmount, ufts.purchase_id as purchaseId, ufts.yield as yield, uuf.fund_info_name_number as fundInfoNameNumber from u_fund_transaction_sell ufts left join f_fund_info ffi on ffi.fund_code = ufts.fund_info_code left join u_user_fund uuf on uuf.fund_info_code = ufts.fund_info_code and ufts.user_id = uuf.user_id where ufts.user_id = #{userId}
and ufts.fund_info_code = #{fundCode}
and ffi.fund_name LIKE concat(concat("%",#{fundName}),"%"))
order by ufts.sell_time desc ,ufts.fund_info_code, ufts.yield limit #{page}, #{limit};

select count(*)
from u_fund_transaction_sell ufts
left join f_fund_info ffi on ffi.fund_code = ufts.fund_info_code
where ufts.user_id = #{userId}

    and ufts.fund_info_code = #{fundCode}


    and ffi.fund_name LIKE concat(concat("%",#{fundName}),"%"))

mapper.update:
UPDATE u_fund_transaction_purchase uftp
SET uftp.state=1,
    uftp.sell_id =(select id
                   from u_fund_transaction_sell ufts
                   where ufts.purchase_id = #{purchaseId}
    )
WHERE uftp.id = #{purchaseId}

UPDATE u_user_fund uuf SET uuf.total_amount =(select ffnwt.purchase_amount from ( SELECT sum(uftp1.purchase_amount) purchase_amount, uftp1.fund_info_code AS fund_info_code, uftp1.user_id FROM u_fund_transaction_purchase uftp1 WHERE uftp1.state = 0 GROUP BY uftp1.fund_info_code, user_id ) ffnwt where uuf.fund_info_code = ffnwt.fund_info_code AND uuf.user_id = ffnwt.user_id),
uuf.total_share =(select ffnwtt.purchase_share from ( SELECT sum(uftp11.purchase_share) purchase_share, uftp11.fund_info_code AS fund_info_code, uftp11.user_id FROM u_fund_transaction_purchase uftp11 WHERE uftp11.state = 0 GROUP BY uftp11.fund_info_code, user_id ) ffnwtt where uuf.fund_info_code = ffnwtt.fund_info_code AND uuf.user_id = ffnwtt.user_id), uuf.new_amount=uuf.total_share * ( select IF ( MAX(ffnwt21.settlement_new_worth) is null, (select IF ( MAX(ffnwt22.fund_net_worth) is null, 0, ffnwt22.fund_net_worth ) from f_fund_net_worth_temp ffnwt22 where ffnwt22.fund_info_code = uuf.fund_info_code ), ffnwt21.settlement_new_worth ) from f_fund_net_worth_temp ffnwt21 where ffnwt21.fund_info_code = uuf.fund_info_code and ffnwt21.fund_day=CURRENT_DATE()
),
uuf.profit_amount=uuf.new_amount - uuf.total_amount,
uuf.cumulative_gain=uuf.profit_amount + (select sum(ufts.income_amount) from u_fund_transaction_sell ufts where ufts.fund_info_code = uuf.fund_info_code and ufts.user_id=uuf.user_id ), uuf.today_profit_amount= uuf.new_amount * (select IF ( MAX(ffnwt3.rise_fall) is null, 0, ffnwt3.rise_fall ) from f_fund_net_worth_temp ffnwt3 where ffnwt3.fund_info_code = uuf.fund_info_code and ffnwt3.fund_day=CURRENT_DATE()
) / 100

mapper.delete:
delete
from u_fund_transaction_purchase
where id = #{id}
  and user_id = #{userId}

delete
from u_fund_transaction_sell
where id = #{id}
  and user_id = #{userId}
